import React from 'react';
import PostCard from './PostCard';
import PostForm from './PostForm';

const Posts = (props) => {
  const { postsData, addPosts, deletePost } = props;

  console.log(props);

  const postsElements = postsData.map(post => <PostCard key={post.id} post={post} deletePost={deletePost}/>)

  return (
    <div>
        {postsElements}
        <PostForm onSubmit={addPosts}/>
    </div>
  );
}

export default Posts;
