import React from 'react';
import { Form, Card } from 'semantic-ui-react'
import { TextArea, InputField } from 'react-semantic-redux-form';
import { Field, reduxForm } from 'redux-form';

const PostForm = (props) => {
  console.log(props.match)

  const { handleSubmit, initialize, editablePost, changeEditablePost } = props;

  React.useEffect(() => {
    if(editablePost) initialize(editablePost)
  }, [editablePost, initialize]);

  return (
    <Card fluid style={{top: '30%'}}>
      <Card.Content>
        <Form>
          <Form.Field>
            <Field 
              name='title' component={InputField}
              label='Title' placeholder='Enter post Title...' />
          </Form.Field>
          <Form.Field>
            <Field 
              name='body' component={TextArea} 
              label='Body' placeholder='Enter post body...' />
          </Form.Field>
          <Form.Button onClick={handleSubmit}>
            {editablePost ? 'Save' : 'Add'}
          </Form.Button>
        </Form>
      </Card.Content>
    </Card>
  );
}

export default reduxForm({form: 'post'})(PostForm);
