import React from 'react';
import { compose } from 'redux';
import { withRouter, NavLink } from 'react-router-dom';
import { Form, Card } from 'semantic-ui-react';
import { TextArea, InputField } from 'react-semantic-redux-form';
import { Field, reduxForm } from 'redux-form';

const PostForm = (props) => {
  const { postsData, handleSubmit, initialize, reset, match: { params: { postId } } } = props;

  React.useEffect(() => {
    if(postId) initialize(postsData[postId])
  }, [postId, postsData, initialize]);

  const onSubmit = () => {
    handleSubmit();
    reset();
  }

  return (
    <Card fluid style={{top: '30%'}}>
      <Card.Content>
        <Form onSubmit={onSubmit}>
          <Form.Field>
            <Field 
              name='title' component={InputField}
              label='Title' placeholder='Enter post Title...' />
          </Form.Field>
          <Form.Field>
            <Field 
              name='body' component={TextArea} 
              label='Body' placeholder='Enter post body...' />
          </Form.Field>
          <Form.Button onClick={onSubmit}>
            {postId ? 'Save' : 'Add'}
          </Form.Button>
          { postId && 
          <NavLink exact to='/' className="ui button" onClick={reset}>
              Cancel
          </NavLink> }
        </Form>
      </Card.Content>
    </Card>
  );
}

export default compose(
  reduxForm({form: 'post'}),
  withRouter,
)(PostForm);
