import React from 'react';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';
import { postsDataSelector, editablePostSelector } from '../redux/app-selectors';
import { getPostsTC, addPostTC, deletePostTC, changeEditablePost, changePost } from '../redux/app-reducer';
import { Grid } from 'semantic-ui-react';
import PostForm from './Posts/PostForm';
import Posts from './Posts/Posts';

const Container = (props) => {
  const { 
    postsData, getPostsData, addPost, 
    deletePost, changeEditablePost, 
    editablePost } = props;

  React.useEffect(()=> {
    getPostsData();
  },[getPostsData])

  return (
    <Grid>
      <Route path='/edit/:postId?' render={() => <PostForm editablePost={editablePost} onSubmit={changePost}/>} />
      <Route exact path='/' render={() => <Posts 
        postsData={postsData}
        addPost={addPost} 
        deletePost={deletePost}
        changeEditablePost={changeEditablePost}
        editablePost={editablePost}/>} />


    </Grid>
  );
}

const mapStateToProps = state => {
  return {
    postsData: postsDataSelector(state),
    editablePost: editablePostSelector(state),
  }
}

const mapDispatchToProps = dispatch => {
  return {
    getPostsData: () => dispatch(getPostsTC()),
    addPost: (post) => dispatch(addPostTC(post)),
    deletePost: (postId) => dispatch(deletePostTC(postId)),
    changeEditablePost: (post) => dispatch(changeEditablePost(post)),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Container);
