import React from 'react';
import { Card, Button } from 'semantic-ui-react'
import { NavLink } from 'react-router-dom';
import Comments from './Comments/Comments';

const PostCard = (props) => {
  const { post, deletePost, changeEditablePost, addComment } = props;

  const path = `/edit/${post.id}`

  return (
  <Card fluid>
    <Card.Content header={post.title} />
    <Card.Content description={post.body} />
    <Card.Content extra textAlign='right'>
      <NavLink to={path} onClick={() => changeEditablePost(post)}>
        <Button primary>
        Edit
        </Button>
      </NavLink> 
      <Button
        secondary 
        onClick={() => deletePost(post.id)}>
          Delete
      </Button>
    </Card.Content>
    <Comments commentsData={post.comments} addComment={addComment} postId={post.id}/>
  </Card>
  );
}

export default PostCard;
