import React from 'react';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';
import { postsDataSelector } from '../redux/app-selectors';
import { getPostsTC, deletePostTC } from '../redux/app-reducer';
import { Grid } from 'semantic-ui-react';
import PostForm from './Posts/PostForm';
import Posts from './Posts/Posts';

const Container = (props) => {
  const { postsData, getPostsData, deletePost } = props;

  React.useEffect(()=> {
    getPostsData();
  },[getPostsData])

  return (
    <Grid>
      {/* <Route path='/edit/:postId?' render={() => <PostForm />} /> */}
      <Posts 
        postsData={postsData} 
        deletePost={deletePost}/>
    </Grid>
  );
}

const mapStateToProps = state => {
  return {
    postsData: postsDataSelector(state),
  }
}

const mapDispatchToProps = dispatch => {
  return {
    getPostsData: () => dispatch(getPostsTC()),
    addPosts: (post) => dispatch(addPostsTC(post)),
    deletePost: (postId) => dispatch(deletePostTC(postId)),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Container);
